mapper.select:
SELECT
FROM ccm_elecfence_alarm a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.device_id LIKE '%'||#{ccmDevice.deviceId}||'%' '%'+#{ccmDevice.deviceId}+'%' concat('%',#{ccmDevice.deviceId},'%')
AND a.people_name LIKE '%'||#{ccmPeople.name}||'%' '%'+#{ccmPeople.name}+'%' concat('%',#{ccmPeople.name},'%')
AND a.alarm_time BETWEEN #{beginCreateDate} AND #{endCreateDate}
AND a.alarm_type = #{alarmType}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

mapper.update:
UPDATE ccm_elecfence_alarm SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
