mapper.select:
SELECT
FROM sys_area a LEFT JOIN ccm_org_area b ON a.id = b.area_id LEFT JOIN sys_area c ON c.id = a.parent_id
WHERE a.id = #{id}

SELECT
FROM sys_area a LEFT JOIN ccm_org_area b ON a.id = b.area_id LEFT JOIN sys_area c ON c.id = a.parent_id
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.code LIKE '%'||#{code}||'%' '%'+#{code}+'%' concat('%',#{code},'%')
AND a.type = #{type}
AND a.parent_id = #{parent.id}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM sys_area a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT AA.id, AA.name2 AS name, sum(AA.cc) AS num, AA.area_point, AA.area_map FROM ( SELECT A.*, B.*, orgarea.area_point, orgarea.area_map FROM ( SELECT COALESCE(sum(people.person_amount),0) + COALESCE(sum(people.oversea_amount),0) + COALESCE(sum(people.float_amount),0) + COALESCE(sum(people.unsettle_amount),0) AS cc, people.area_id, area1. NAME AS name1, area1.parent_ids FROM ccm_people_amount people, sys_area area1 WHERE people.area_id = area1.id AND people.del_flag = #{delFlag} and DATE_FORMAT(people.amount_date, '%Y%m') = DATE_FORMAT(CURDATE() , '%Y%m')
AND area1.type = #{type}
GROUP BY people.area_id ) A RIGHT JOIN ( SELECT area2.id, area2. NAME AS name2 FROM sys_area area2 WHERE 1=1
and (${more1})
) B ON A.area_id = B.id LEFT JOIN ccm_org_area orgarea ON B.id = orgarea.area_id ) AA GROUP BY AA.id

mapper.update:
UPDATE sys_area SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT a.id AS "id", a.parent_id AS "parent.id", a.parent_ids AS "parentIds", a.name AS "name", a.sort AS "sort", a.code AS "code", a.type AS "type", c.name AS "parent.name", a.create_by AS "createBy.id", a.create_date AS "createDate", a.update_by AS "updateBy.id", a.update_date AS "updateDate", a.del_flag AS "delFlag" FROM sys_area a LEFT JOIN sys_area c ON c.id = a.parent_id
a.del_flag = #{DEL_FLAG_NORMAL}
AND ( a.id = #{parentIds} OR a.parent_ids LIKE '%'||#{parentIds}||'%' '%'+#{parentIds}+'%' concat('%',#{parentIds},'%') )
